function verificaExperimento($idusuario,$especiesReflora,$Exp)
{
	while (list ($key,$val) = @each($especiesReflora)) {
		$valor = explode(' ',$val);

		$Exp->name='REFLORA: '.$valor[0];
		$Exp->description=$valor[0];
		$Exp->group='';
		$Exp->iduser=$idusuario;
		$Exp->type='terrestre';
		$Exp->automaticfilter='false';

		$sql = "select * from modelr.experiment where name = 'REFLORA: ".$valor[0]."'";
		$res = pg_exec($sql);
		if (pg_num_rows($res)<1)
		{
			// experimento não existe. Vou cadastrar com o nome REFLORA: XXXXX
			echo $Exp->incluir();
			echo "<br>";
		}
    }
}

